package ocrFaktury;import java.util.*;public class FiltrDanych {    private final Map<String, List<String>> dane = new LinkedHashMap<>();    public void dodaj(String pole, String wartosc) {        dane.computeIfAbsent(pole, k -> new ArrayList<>()).add(wartosc);    }    public String get(String pole) {        List<String> lista = dane.get(pole);        if (lista == null || lista.isEmpty()) return "";        return String.join(" ", lista);    }    public Map<String, List<String>> getWszystkieListy() {        Map<String, List<String>> kopia = new LinkedHashMap<>();        for (Map.Entry<String, List<String>> entry : dane.entrySet()) {            kopia.put(entry.getKey(), new ArrayList<>(entry.getValue()));        }        return kopia;    }    public String getPodsumowanie() {        StringBuilder sb = new StringBuilder();        for (Map.Entry<String, List<String>> entry : dane.entrySet()) {            sb.append(entry.getKey()).append(": ");            sb.append(String.join(" ", entry.getValue())).append("\n");        }        return sb.toString();    }    public void reset() {        dane.clear();    }}