package ocrFaktury.dataBase;import ocrFaktury.dataBase.FakturaDAO;import javafx.scene.Scene;import javafx.scene.control.TableColumn;import javafx.scene.control.TableView;import javafx.scene.control.cell.PropertyValueFactory;import javafx.scene.layout.VBox;import javafx.stage.Stage;public class BazaDanychView {    public void show() {        Stage stage = new Stage();        stage.setTitle("Baza Danych Faktur");        TableView<FakturaRecord> table = new TableView<>();        TableColumn<FakturaRecord, Integer> idCol = new TableColumn<>("ID");        idCol.setCellValueFactory(new PropertyValueFactory<>("id"));        TableColumn<FakturaRecord, String> numerCol = new TableColumn<>("Numer Faktury");        numerCol.setCellValueFactory(new PropertyValueFactory<>("numerFaktury"));        TableColumn<FakturaRecord, String> dataCol = new TableColumn<>("Data");        dataCol.setCellValueFactory(new PropertyValueFactory<>("data"));        table.getColumns().addAll(idCol, numerCol, dataCol);        FakturaDAO dao = new FakturaDAO();        table.getItems().addAll(dao.getAllFaktury());        VBox root = new VBox(table);        Scene scene = new Scene(root, 600, 400);        stage.setScene(scene);        stage.show();    }}