package ocrFaktury.dataBase;public class FakturaRecord {    private int id;    private String numerFaktury;    private String data;    public FakturaRecord(int id, String numerFaktury, String data) {        this.id = id;        this.numerFaktury = numerFaktury;        this.data = data;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getNumerFaktury() {        return numerFaktury;    }    public void setNumerFaktury(String numerFaktury) {        this.numerFaktury = numerFaktury;    }    public String getData() {        return data;    }    public void setData(String data) {        this.data = data;    }}