package ocrFaktury.login;import javafx.geometry.Pos;import javafx.scene.Scene;import javafx.scene.control.*;import javafx.scene.image.Image;import javafx.scene.image.ImageView;import javafx.scene.input.KeyCode;import javafx.scene.layout.*;import javafx.scene.paint.Color;import javafx.scene.text.Font;import javafx.scene.text.Text;import javafx.stage.Stage;public class LoginView {    public void show(Stage primaryStage, Runnable onLoginSuccess) {        VBox loginBox = new VBox(20);        loginBox.setAlignment(Pos.CENTER);        loginBox.setMaxWidth(400);        loginBox.setStyle("-fx-padding: 40; -fx-background-radius: 20;");        Text title = new Text("HIGH HAWK OCR SYSTEM");        title.setFont(Font.font("Arial", 36));        title.setFill(Color.WHITE);        TextField usernameField = new TextField();        usernameField.setPromptText("Login");        usernameField.setFont(Font.font(18));        usernameField.setMaxWidth(300);        PasswordField passwordField = new PasswordField();        passwordField.setPromptText("Hasło");        passwordField.setFont(Font.font(18));        passwordField.setMaxWidth(300);        Button loginButton = new Button("Zaloguj");        loginButton.setFont(Font.font(20));        loginButton.setStyle("-fx-background-color: #a93226; -fx-text-fill: white; -fx-background-radius: 10px; -fx-padding: 10 30;");        loginButton.setOnMouseEntered(e -> loginButton.setStyle("-fx-background-color: #c0392b; -fx-text-fill: white; -fx-background-radius: 10px; -fx-padding: 10 30;"));        loginButton.setOnMouseExited(e -> loginButton.setStyle("-fx-background-color: #a93226; -fx-text-fill: white; -fx-background-radius: 10px; -fx-padding: 10 30;"));        loginButton.setOnAction(e -> {            String username = usernameField.getText();            String password = passwordField.getText();            if ("admin".equals(username) && "admin".equals(password)) {                onLoginSuccess.run();                primaryStage.close();            } else {                Alert alert = new Alert(Alert.AlertType.ERROR);                alert.setTitle("Błąd logowania");                alert.setHeaderText(null);                alert.setContentText("Niepoprawny login lub hasło.");                alert.showAndWait();            }        });        loginBox.setOnKeyPressed(event -> {            if (event.getCode() == KeyCode.ENTER) {                loginButton.fire();            }        });        loginBox.getChildren().addAll(title, usernameField, passwordField, loginButton);        StackPane root = new StackPane();        try {            var url = getClass().getResource("/images/DC29B025-62D5-455D-8CE8-A5A984655847.PNG");            if (url == null) {                throw new IllegalArgumentException("Nie znaleziono obrazu: /images/DC29B025-62D5-455D-8CE8-A5A984655847.PNG");            }            Image background = new Image(url.toExternalForm());            ImageView backgroundView = new ImageView(background);            backgroundView.setPreserveRatio(false);            backgroundView.setOpacity(1.0);            backgroundView.setManaged(false);            backgroundView.fitWidthProperty().bind(root.widthProperty());            backgroundView.fitHeightProperty().bind(root.heightProperty());            root.getChildren().addAll(backgroundView, loginBox);        } catch (Exception e) {            e.printStackTrace();            root.setStyle("-fx-background-color: linear-gradient(to bottom, #444, #222);");            root.getChildren().add(loginBox);        }        Scene scene = new Scene(root, 1280, 720);        primaryStage.setScene(scene);        primaryStage.setTitle("Logowanie - High Hawk");        primaryStage.show();    }}